def newPassHelper(a, b):
    lenA = len(a)
    lenB = len(b)
    result = []
    i, shorterLen = 0, min(lenA, lenB)
    while i < shorterLen:
        result.append(a[i])
        result.append(b[i])
        i += 1
    if lenA > lenB:
        result.extend(a[i:])
    else:
        if i < len(b):
            result.extend(b[i:])

    return "".join(result)


