def newPassword(a, b):
    # Write your code here
    lenA = len(a)
    lenB = len(b)
    result = []
    if lenA == lenB:
        for i in range(lenA):
            result.append(a[i])
            result.append(b[i])
    elif lenA > lenB:
        result = newPassHelper(a, b)
    else:  # lenB > lenA
        result = newPassHelper(b, a)

    return "".join(result)


if __name__ == "__main__":
    print(
        newPassHelper(
            "cat",
            "",
        )
    )
